import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';

function Navigation() {
  const [scrolled, setScrolled] = useState(false);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 40);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const scrollTo = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: 'smooth' });
    setOpen(false);
  };

  const links = [
    { name: 'Home', id: 'home' },
    { name: 'Services', id: 'services' },
    { name: 'Portfolio', id: 'portfolio' },
    { name: 'About', id: 'about' },
    { name: 'Contact', id: 'contact' }
  ];

  return (
    <>
      {/* MAIN NAVBAR */}
      <nav className="fixed top-6 left-1/2 -translate-x-1/2 z-50">
        <div
          className={`flex items-center gap-6 px-6 py-3 rounded-full
          backdrop-blur-xl border border-white/10
          transition-all duration-300
          ${scrolled ? 'bg-black/80 shadow-lg' : 'bg-black/60'}`}
        >
          {/* LOGO */}
          <div
            onClick={() => scrollTo('home')}
            className="text-white font-bold text-lg cursor-pointer"
          >
            <span className="text-blue-500">NeX</span>Let
          </div>

          {/* DESKTOP LINKS */}
          <div className="hidden md:flex items-center gap-6">
            {links.map(link => (
              <button
                key={link.id}
                onClick={() => scrollTo(link.id)}
                className="text-sm text-gray-300 hover:text-white transition"
              >
                {link.name}
              </button>
            ))}
          </div>

          {/* CTA */}
          <button
            onClick={() => scrollTo('contact')}
            className="hidden md:block bg-blue-500 hover:bg-blue-600 text-white text-sm px-5 py-2 rounded-full transition"
          >
            Get Started
          </button>

          {/* MOBILE MENU BUTTON */}
          <button
            onClick={() => setOpen(!open)}
            className="md:hidden text-white"
          >
            {open ? <X size={22} /> : <Menu size={22} />}
          </button>
        </div>
      </nav>

      {/* MOBILE DROPDOWN (WEBZY STYLE) */}
      {open && (
        <div className="fixed inset-0 z-40 flex items-start justify-center pt-28">
          <div
            className="bg-black/90 backdrop-blur-2xl border border-white/10
            rounded-2xl w-[90%] max-w-sm p-6 space-y-4 shadow-2xl"
          >
            {links.map(link => (
              <button
                key={link.id}
                onClick={() => scrollTo(link.id)}
                className="block w-full text-left text-gray-300 hover:text-white
                px-4 py-3 rounded-lg hover:bg-white/10 transition"
              >
                {link.name}
              </button>
            ))}

            <button
              onClick={() => scrollTo('contact')}
              className="w-full mt-3 bg-blue-500 hover:bg-blue-600
              text-white py-3 rounded-xl transition"
            >
              Get Started
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default Navigation;
