import React, { useEffect, useState } from "react";
import { Menu, X } from "lucide-react";

function Navigation() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const onScroll = () => setIsScrolled(window.scrollY > 40);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollTo = (id) => {
    const el = document.getElementById(id);
    if (el) {
      el.scrollIntoView({ behavior: "smooth" });
      setOpen(false);
    }
  };

  const links = [
    { name: "Services", id: "services" },
    { name: "Portfolio", id: "portfolio" },
    { name: "About", id: "about" },
    { name: "Contact", id: "contact" },
  ];

  return (
    <>
      {/* Floating Navbar */}
      <nav className="fixed top-4 left-1/2 -translate-x-1/2 z-50 w-full px-4">
        <div
          className={`mx-auto max-w-fit rounded-full transition-all duration-300
          ${isScrolled ? "bg-black/70 backdrop-blur-xl shadow-xl" : "bg-black/40 backdrop-blur-lg"}
          `}
        >
          <div className="flex items-center gap-6 px-6 py-3">

            {/* Logo */}
            <button
              onClick={() => scrollTo("home")}
              className="text-xl font-bold text-white"
            >
              <span className="text-blue-500">NeX</span>Let
            </button>

            {/* Desktop Links */}
            <div className="hidden md:flex items-center gap-6">
              {links.map((link) => (
                <button
                  key={link.id}
                  onClick={() => scrollTo(link.id)}
                  className="text-sm text-gray-300 hover:text-white transition"
                >
                  {link.name}
                </button>
              ))}

              <button
                onClick={() => scrollTo("contact")}
                className="ml-2 rounded-full bg-blue-500 px-5 py-2 text-sm font-medium text-white hover:bg-blue-600 transition"
              >
                Get Started
              </button>
            </div>

            {/* Mobile Toggle */}
            <button
              className="md:hidden text-white"
              onClick={() => setOpen(!open)}
            >
              {open ? <X size={22} /> : <Menu size={22} />}
            </button>
          </div>
        </div>
      </nav>

      {/* Mobile Menu */}
      {open && (
        <div className="fixed inset-0 z-40 bg-black/80 backdrop-blur-md md:hidden">
          <div className="flex h-full flex-col items-center justify-center gap-6">
            {links.map((link) => (
              <button
                key={link.id}
                onClick={() => scrollTo(link.id)}
                className="text-lg text-gray-300 hover:text-white transition"
              >
                {link.name}
              </button>
            ))}
            <button
              onClick={() => scrollTo("contact")}
              className="rounded-full bg-blue-500 px-6 py-2 text-white hover:bg-blue-600 transition"
            >
              Get Started
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default Navigation;
