import React, { useState } from 'react';

function Portfolio() {
  const [portfolioItems] = useState([
    {
      id: 1,
      title: 'E-Commerce Platform',
      description: 'Full-stack e-commerce solution with payment integration',
      category: 'Web Development',
      image: '/portfolio-1.jpg'
    },
    {
      id: 2,
      title: 'Mobile App Design',
      description: 'UI/UX design for iOS and Android applications',
      category: 'Design',
      image: '/portfolio-2.jpg'
    },
    {
      id: 3,
      title: 'Brand Identity',
      description: 'Complete branding and logo design package',
      category: 'Branding',
      image: '/portfolio-3.jpg'
    }
  ]);

  return (
    <div className="py-20 px-4 md:px-8 bg-gray-900">
      <h2 className="text-4xl font-bold mb-12 text-center text-white">Our Portfolio</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {portfolioItems && portfolioItems.length > 0 ? (
          portfolioItems.map((item) => (
            <div key={item.id} className="bg-gray-800 rounded-lg overflow-hidden hover:shadow-lg transition">
              <div className="h-48 bg-gray-700"></div>
              <div className="p-6">
                <h3 className="text-xl font-semibold text-white mb-2">{item.title}</h3>
                <p className="text-sm text-gray-400 mb-3">{item.category}</p>
                <p className="text-gray-300">{item.description}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-400">No portfolio items available</p>
        )}
      </div>
    </div>
  );
}

export default Portfolio;
