import React, { useState, useEffect } from 'react';
import { Button } from './ui/button';

function Navigation() {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 40);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: 'smooth' });
  };

  const navLinks = [
    { name: 'Services', id: 'services' },
    { name: 'Portfolio', id: 'portfolio' },
    { name: 'About', id: 'about' },
    { name: 'Contact', id: 'contact' }
  ];

  return (
    <nav className="fixed top-6 left-1/2 -translate-x-1/2 z-50">
      <div
        className={`
          flex items-center
          gap-4 sm:gap-6
          px-4 sm:px-6
          py-2.5rounded-full
          border border-white/10
          backdrop-blur-xl
          transition-all duration-300
          ${isScrolled ? 'bg-black/80 shadow-lg' : 'bg-black/60'}
        `}
      >
        {/* LOGO (HOME) */}
        <button
          onClick={() => scrollToSection('home')}
          className="text-sm sm:text-base font-bold text-white whitespace-nowrap"
        >
          <span className="text-blue-500">NeX</span>Let
        </button>

        {/* NAV LINKS */}
        <div className="flex items-center gap-3 sm:gap-5">
          {navLinks.map(link => (
            <button
              key={link.id}
              onClick={() => scrollToSection(link.id)}
              className="text-xs sm:text-sm text-gray-300 hover:text-white transition whitespace-nowrap"
            >
              {link.name}
            </button>
          ))}
        </div>


      </div>
    </nav>
  );
}

export default Navigation;
